--  DDL for View RIS_V_POULE_JAAR
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "RIS_OWNER"."RIS_V_POULE_JAAR" ("POULE", "JAAR", "SOORT", "KEY") AS 
  select distinct tem.poule, tem.jaar, tem.soort, tem.jaar || tem.soort || tem.poule as key
        from ris_teams tem
        order by jaar, soort, poule
;
--------------------------------------------------------
--  DDL for View RIS_V_WEDSTRIJDEN
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "RIS_OWNER"."RIS_V_WEDSTRIJDEN" ("VOLGNR", "DATUM", "TIJDSTIP", "POULE", "PUNTEN_VOOR", "PUNTEN_TEGEN", "DISPLAY_THUIS", "DISPLAY_UIT", "SOORT", "OPMERKING", "SCHEIDSRECHTERS", "WEDSTRIJD_NUMMER", "TYPE_WEDSTRIJD", "JAAR") AS 
  select WKS.volgnr
     , WKS.datum
     , WKS.tijdstip
     , null as poule
     , WKS.punten_voor
     , WKS.punten_tegen
     , decode(WKS.THUIS_TEAM, null, WKS.poule_thuis || WKS.poule_thuis_nr, TEM.NAAM||' <SMALL>('||WKS.poule_thuis || WKS.poule_thuis_nr ||')</SMALL>') as display_thuis
     , decode(WKS.UIT_TEAM  , null, WKS.poule_uit   || WKS.poule_uit_nr , TEM2.NAAM||' <SMALL>('||WKS.poule_uit || WKS.poule_uit_nr || ')</SMALL>') as display_uit
     , WKS.soort
     , WKS.opmerking
     , WKS.scheidsrechters
     , to_number(WKS.wedstrijd_nummer) as wedstrijd_nummer
     , 'Kruis' as type_wedstrijd
     , WKS.jaar as jaar
  from ris_wedstrijden_kruis WKS
     , ris_teams TEM
     , ris_teams TEM2
 where WKS.THUIS_TEAM = TEM.volgnr (+)
   and WKS.UIT_TEAM = TEM2.volgnr (+)
   and WKS.JAAR = TEM.JAAR (+)
   and WKS.JAAR = TEM2.JAAR (+)
UNION
select WPE.volgnr
     , WPE.datum
     , WPE.tijdstip
     , TEM.POULE as poule
     , WPE.punten_voor
     , WPE.punten_tegen
     , TEM.NAAM as display_thuis
     , TEM2.NAAM as display_uit
     , WPE.soort
     , WPE.opmerking
     , WPE.scheidsrechters
     , to_number(WPE.wedstrijd_nummer) as wedstrijd_nummer
     , 'Poule' as type_wedstrijd
     , WPE.jaar as jaar
  from ris_wedstrijden_poule WPE
     , ris_teams TEM
     , ris_teams TEM2
 where WPE.TEAM_VOLGNR = TEM.volgnr
   and WPE.JAAR = TEM.JAAR
   and WPE.TEAM2_VOLGNR = TEM2.volgnr
   and WPE.JAAR = TEM2.JAAR
UNION
select WFE.volgnr
     , WFE.datum
     , WFE.tijdstip
     , null as poule
     , WFE.punten_voor
     , WFE.punten_tegen
     , decode(WFE.thuis_nr, null, decode(WFE.THUIS_WV, 'Winnaar', 'Winnaar/Winner', 'Verliezer/Loser') ||' Nr.'||to_char(WFE.thuis_wed), TEM.Naam ||' <SMALL>('||decode(WFE.THUIS_WV, 'Winnaar', 'Winnaar/Winner', 'Verliezer/Loser') ||' Nr.'||to_char(WFE.thuis_wed)||')</SMALL>') as display_thuis
     , decode(WFE.uit_nr, null, decode(WFE.UIT_WV, 'Winnaar', 'Winnaar/Winner', 'Verliezer/Loser') ||' Nr.'||to_char(WFE.uit_wed), TEM2.Naam ||' <SMALL>('||decode(WFE.UIT_WV, 'Winnaar', 'Winnaar/Winner', 'Verliezer/Loser') ||' Nr.'||to_char(WFE.uit_wed)||')</SMALL>') as display_uit
     , WFE.soort
     , WFE.opmerking
     , WFE.scheidsrechters
     , to_number(WFE.wedstrijd_nummer) as wedstrijd_nummer
     , 'Finale' as type_wedstrijd
     , WFE.jaar as jaar
  from ris_wedstrijden_finale WFE
     , ris_teams TEM
     , ris_teams TEM2
 where WFE.THUIS_NR = TEM.VOLGNR (+)
   and WFE.JAAR = TEM.JAAR (+)
   and WFE.UIT_NR = TEM2.VOLGNR (+)
   and WFE.JAAR = TEM2.JAAR (+)
UNION
select WMX.volgnr
     , WMX.datum
     , WMX.tijdstip
     , null as poule
     , WMX.punten_voor
     , WMX.punten_tegen
     , decode( WMX.type_thuis
             , 'Kruis'
             , decode( WMX.THUIS_TEAM
         		         , null
        		         , WMX.poule_thuis
        		           || WMX.poule_thuis_nr
        		         , TEM_THUIS_KRUIS.NAAM
        		           || ' <SMALL>('
        		           || WMX.poule_thuis
        		           || WMX.poule_thuis_nr
        		           || ')</SMALL>'
        		         )
             , decode( WMX.thuis_nr
		                 , null
		                 , decode( WMX.THUIS_WV
			                       , 'Winnaar'
			                       , 'Winnaar/Winner'
			                       , 'Verliezer/Loser'
			                       )
        		           || ' Nr.'
		                   || to_char(WMX.thuis_wed)
                     , TEM_THUIS_FINALE.Naam
		                   || ' <SMALL>('
		                   || decode( WMX.THUIS_WV
			                          , 'Winnaar'
			                          , 'Winnaar/Winner'
			                          , 'Verliezer/Loser'
			                          )
		                   || ' Nr.'
		                   || to_char(WMX.thuis_wed)
		                   || ')</SMALL>'
		                 )
	           ) as display_thuis
     , decode( WMX.type_uit
             , 'Kruis'
	           , decode( WMX.UIT_TEAM
		                 , null
		                 , WMX.poule_uit
		                   || WMX.poule_uit_nr
		                 , TEM_UIT_KRUIS.NAAM
		                   || ' <SMALL>('
		                   || WMX.poule_uit
		                   || WMX.poule_uit_nr
		                   || ')</SMALL>'
		                 )
	           , decode( WMX.uit_nr
		                 , null
		                 , decode( WMX.UIT_WV
			                       , 'Winnaar'
			                       , 'Winnaar/Winner'
			                       , 'Verliezer/Loser'
			                       )
		                   || ' Nr.'
		                   || to_char(WMX.uit_wed)
		                 , TEM_UIT_FINALE.Naam
		                   || ' <SMALL>('
		                   || decode( WMX.UIT_WV
			                          , 'Winnaar'
			                          , 'Winnaar/Winner'
			                          , 'Verliezer/Loser'
			                          )
		                   || ' Nr.'
		                   || to_char(WMX.uit_wed)
		                   || ')</SMALL>'
		                 )
	            ) as display_uit
     , WMX.soort
     , WMX.opmerking
     , WMX.scheidsrechters
     , to_number(WMX.wedstrijd_nummer) as wedstrijd_nummer
     , 'Mix' as type_wedstrijd
     , WMX.jaar as jaar
  from ris_wedstrijden_mix WMX
     , ris_teams TEM_THUIS_FINALE
     , ris_teams TEM_UIT_FINALE
     , ris_teams TEM_THUIS_KRUIS
     , ris_teams TEM_UIT_KRUIS
 where WMX.THUIS_NR = TEM_THUIS_FINALE.VOLGNR (+)
   and WMX.UIT_NR = TEM_UIT_FINALE.VOLGNR (+)
   and WMX.THUIS_TEAM = TEM_THUIS_KRUIS.volgnr (+)
   and WMX.UIT_TEAM = TEM_UIT_KRUIS.VOLGNR (+)
   and WMX.JAAR = TEM_THUIS_FINALE.JAAR (+)
   and WMX.JAAR = TEM_UIT_FINALE.JAAR (+)
