  -----------------------------------------------------------------------------------------
  procedure start_sync_uit is
  
  begin
    v_output_file := utl_file.fopen('D:\Sync\SyncXML\classes'
                                   ,'homeplate.xml'
                                   ,'W'
                                   ,32000);
    utl_file.put_line(v_output_file
                     ,'<?xml version = ' || '''' || '1.0' || '''' || ' encoding = ' || '''' ||
                      'iso-8859-1' || '''' || '?>');
  
    utl_file.put_line(v_output_file
                     ,'<xmlseries>');
  
    sync_mail_berichten;
  
    utl_file.put_line(v_output_file
                     ,'</xmlseries>');
    utl_file.fclose(v_output_file);
  exception
    when others then
      dbms_output.put_line('start_sync_in -> Fout: ' || SQLCODE);
  end;
  -----------------------------------------------------------------------------------------
  procedure sync_mail_berichten is
  
    v_s_volgnr varchar2(100);
  
    cursor c_berichten is
      select mbrt.volgnr
            ,replace(replace(mbrt.onderwerp
                            ,'&'
                            ,'&amp;')
                    ,'"'
                    ,'''') as onderwerp
            ,replace(replace(mbrt.berichttekst
                            ,'&'
                            ,'&amp;')
                    ,'"'
                    ,'''') as berichttekst
            ,replace(replace(mbrt.adres
                            ,'&'
                            ,'&amp;')
                    ,'"'
                    ,'''') as adres
            ,mbrt.datum_bericht
            ,mbrt.gbrvolgnr
        from ris_owner.mailbericht mbrt
       where mbrt.datum_verwerkt is null
       order by mbrt.volgnr;
  
  begin
    dbms_output.put_line('Synchroniseren van berichten');
    for r_berichten in c_berichten loop
      v_s_volgnr := to_char(r_berichten.volgnr);
    
      utl_file.put_line(v_output_file
                       ,'  <mailbericht volgnr="' || to_char(r_berichten.volgnr) || '"');
      utl_file.put_line(v_output_file
                       ,'               onderwerp="' || r_berichten.onderwerp || '"');
      utl_file.put_line(v_output_file
                       ,'               berichttekst="' || r_berichten.berichttekst || '"');
      utl_file.put_line(v_output_file
                       ,'               adres="' || r_berichten.adres || '"');
      utl_file.put_line(v_output_file
                       ,'               datum_bericht="' ||
                        to_char(r_berichten.datum_bericht
                               ,'DD-MM-YYYY') || '"');
      utl_file.put_line(v_output_file
                       ,'               gbrvolgnr="' || to_char(r_berichten.gbrvolgnr) ||
                        '" />');
    
      update ris_owner.mailbericht
         set datum_verwerkt = sysdate
       where volgnr = r_berichten.volgnr;
    
      commit;
    end loop;
    ---------------------------------------------------------------------------------------
  exception
    when others then
      dbms_output.put_line('sync_mail_berichten -> Fout bij volgnr: ' || v_s_volgnr || '-' ||
                           SQLCODE);
  end;
  -----------------------------------------------------------------------------------------
end bsm_pck_sync_xml;
