  procedure foutafhandeling(p_onderdeel   in varchar2,
                            p_sqlcode     in varchar2,
                            p_sqlerrmsg   in varchar2,
                            p_additioneel in varchar2) is
  
  begin
    insert into ris_errorlog
      (datum, onderdeel, sql_code, sql_errmsg, additioneel)
