  procedure verwerk_login(p_s_userid   in varchar2
                         ,p_s_password in varchar2
                         ,p_jaar       in varchar2) is
  
    v_s_session_id varchar2(100) := null;
  
    cursor c_gebruiker is
      select *
        from ris_gebruikers
       where loginid = p_s_userid and wachtwoord = p_s_password;
  
    r_gebruiker c_gebruiker%rowtype;
  
  begin
    open c_gebruiker;
    fetch c_gebruiker
      into r_gebruiker;
    if c_gebruiker%NOTFOUND then
      htp.p('userid or password not correct!<BR>');
      login(p_jaar);
    else
      v_s_session_id := ris_pck_alg.genereerSessionId(to_char(r_gebruiker.volgnr));
    
      update ris_gebruikers gbr
         set gbr.laatste_login = sysdate
            ,gbr.aantal_logins = nvl(r_gebruiker.aantal_logins
                                    ,0) + 1
       where gbr.volgnr = r_gebruiker.volgnr;
    
      frame(to_char(r_gebruiker.volgnr)
           ,'start'
           ,v_s_session_id
           ,p_jaar);
      --      start_mail(to_char(r_gebruiker.volgnr));
    end if;
  end;
