  procedure verwerk_register(p_s_team       in varchar2
                            ,p_s_firstname  in varchar2
                            ,p_s_middlename in varchar2
                            ,p_s_lastname   in varchar2
                            ,p_s_userid     in varchar2
                            ,p_s_password1  in varchar2
                            ,p_s_password2  in varchar2
                            ,p_jaar         in varchar2) is
  
    v_b_controle   boolean := true;
    v_s_dummy      varchar2(1);
    v_n_new_value  number;
    v_s_session_id varchar2(100) := null;
    v_n_foutcode   number := 0;
  
    cursor c_gebruiker is
      select 'x'
        from ris_gebruikers
       where loginid = p_s_userid and jaar = to_number(p_jaar);
  
    cursor c_new_value is
      select gbr_seq.nextval
        from dual;
  
  begin
  
    -- Controle op passwords gelijk zijn
    if p_s_password1 != p_s_password2 then
      v_b_controle := false;
      v_n_foutcode := 1;
    end if;
  
    -- Controle of userid al bestaat
    open c_gebruiker;
    fetch c_gebruiker
      into v_s_dummy;
    if c_gebruiker%found then
      v_b_controle := false;
      v_n_foutcode := 2;
    end if;
  
    if v_b_controle then
      open c_new_value;
      fetch c_new_value
        into v_n_new_value;
      close c_new_value;
      insert into ris_gebruikers
        (volgnr
        ,loginid
        ,wachtwoord
        ,voornaam
        ,tussenvoegsel
        ,achternaam
        ,temvolgnr
        ,jaar)
      values
        (v_n_new_value
        ,p_s_userid
        ,p_s_password1
        ,p_s_firstname
        ,p_s_middlename
        ,p_s_lastname
        ,to_number(p_s_team)
        ,to_number(p_jaar));
      commit;
    
      v_s_session_id := ris_pck_alg.genereersessionid(to_char(v_n_new_value));
      frame(to_char(v_n_new_value)
           ,'start'
           ,v_s_session_id
           ,p_jaar);
      --      start_mail(to_char(v_n_new_value));
    else
      htp.headopen;
      ris_pck_alg.stylesheet;
      ris_pck_alg.norightclick;
    
      htp.bodyopen;
      htp.p('<center>');
      htp.p('<img border=0 src="' || ris_pck_alg.geef_syspar('DIR_IMAGES') ||
            'l_s_bbs.jpg" align=absbottom><font size=6>&nbsp;&nbsp;Register Form Mail</font>');
      htp.br;
      htp.br;
      htp.p('<h1><font color="RED">Fout / Error</font></h1>');
      htp.br;
    
      if v_n_foutcode = 1 then
        htp.p('De wachtwoorden zijn niet gelijk.');
        htp.br;
        htp.br;
        htp.p('The password do not match.');
        htp.br;
        htp.br;
      end if;
      if v_n_foutcode = 2 then
        htp.p('De opgegeven gebruikerId bestaat al. Bedenk een andere.');
        htp.br;
        htp.br;
        htp.p('The provided userid already exists. Use a different one.');
        htp.br;
        htp.br;
      end if;
      htp.p('<a href="javascript:history.back()">Terug / Go Back</a>');
      htp.bodyclose;
      htp.htmlclose;
    end if;
  end;
