  procedure frame(p_s_gbr_volgnr in varchar2
                 ,p_s_type       in varchar2
                 ,p_s_session_id in varchar2
                 ,p_jaar         in varchar2) is
  
  begin
    if ris_pck_alg.controlesessionid(p_s_gbr_volgnr
                                    ,p_s_session_id) then
      if p_s_type = 'start' then
        htp.framesetopen(ccols => '25%,75%');
        htp.frame('ris_pck_mail.toon_berichtenbak?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
                  '&p_s_session_id=' || p_s_session_id || '&p_jaar=' || p_jaar
                 ,'BAK');
        htp.frame('ris_pck_mail.startscherm?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
                  '&p_s_session_id=' || p_s_session_id || '&p_jaar=' || p_jaar
                 ,'BERICHT');
        htp.framesetclose;
      end if;
      if p_s_type = 'nieuwbericht' then
        htp.framesetopen(ccols => '25%,75%');
        htp.frame('ris_pck_mail.toon_berichtenbak?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
                  '&p_s_session_id=' || p_s_session_id || '&p_jaar=' || p_jaar
                 ,'BAK');
        htp.frame('ris_pck_mail.nieuw_bericht?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
                  '&p_s_session_id=' || p_s_session_id || '&p_jaar=' || p_jaar
                 ,'BERICHT');
        htp.framesetclose;
      end if;
      if p_s_type = 'inbox' then
        htp.framesetopen(ccols => '25%,75%');
        htp.frame('ris_pck_mail.toon_berichtenbak?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
                  '&p_s_session_id=' || p_s_session_id || '&p_jaar=' || p_jaar
                 ,'BAK');
        htp.framesetopen(crows => '45%,55%');
        htp.frame('ris_pck_mail.toon_berichten?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
                  '&p_s_bbk_volgnr=1&p_s_session_id=' || p_s_session_id || '&p_jaar=' ||
                  p_jaar
                 ,'BERICHT');
        htp.frame('ris_pck_mail.leeg'
                 ,'DETAIL');
        htp.framesetclose;
        htp.framesetclose;
      end if;
      if p_s_type in ('1', '2', '3', '4') then
        htp.framesetopen(ccols => '25%,75%');
        htp.frame('ris_pck_mail.toon_berichtenbak?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
                  '&p_s_session_id=' || p_s_session_id || '&p_jaar=' || p_jaar
                 ,'BAK');
        htp.framesetopen(crows => '45%,55%');
        htp.frame('ris_pck_mail.toon_berichten?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
                  '&p_s_bbk_volgnr=' || p_s_type || '&p_s_session_id=' || p_s_session_id ||
                  '&p_jaar=' || p_jaar
                 ,'BERICHT');
        htp.frame('ris_pck_mail.leeg'
                 ,'DETAIL');
        htp.framesetclose;
        htp.framesetclose;
      end if;
    end if;
  end;
