  ----------------------------------------------------------------------------------------
  procedure toon_berichtenbak(p_s_gbr_volgnr in varchar2
                             ,p_s_session_id in varchar2
                             ,p_jaar         in varchar2) is
  
    v_n_bak_volgnr number;
    v_n_aantal     number;
    v_s_sync_tijd  varchar2(100) := null;
    v_s_send_tijd  varchar2(100) := null;
  
    cursor c_berichtenbak is
      select bak.*
        from ris_berichtenbak bak;
  
    cursor c_aantal_berichten is
      select count(1) aantal
        from ris_bericht ber
       where ber.gbrvolgnr = to_number(p_s_gbr_volgnr) and ber.bbkvolgnr = v_n_bak_volgnr and
             ber.status <> 'G' and ber.jaar = to_number(p_jaar);
  
    cursor c_send_tijd is
      select waarde
        from ris_sysvar
       where code = 'SEND_TIME';
  
    cursor c_sync_tijd is
      select waarde
        from ris_sysvar
       where code = 'SYNC_TIME';
  
  begin
    if ris_pck_alg.controlesessionid(p_s_gbr_volgnr
                                    ,p_s_session_id) then
      htp.htmlopen;
      htp.headopen;
      ris_pck_alg.titel;
      ris_pck_alg.stylesheet;
    
      htp.p('<script>
               function toonBerichten(v_berichtenbak) {
                 parent.location.href="ris_pck_mail.frame?p_s_gbr_volgnr=' ||
            p_s_gbr_volgnr || '&p_s_type="+v_berichtenbak+"&p_s_session_id=' ||
            p_s_session_id || '&p_jaar=' || p_jaar || '";
               }
               function toonNaam() {
                 parent.location.href="ris_pck_mail.frame?p_s_gbr_volgnr=' ||
            p_s_gbr_volgnr || '&p_s_type=start&p_s_session_id=' || p_s_session_id ||
            '&p_jaar=' || p_jaar || '";
               }
               function nieuwBericht() {
                 parent.location.href="ris_pck_mail.frame?p_s_gbr_volgnr=' ||
            p_s_gbr_volgnr || '&p_s_type=nieuwbericht&p_s_session_id=' || p_s_session_id ||
            '&p_jaar=' || p_jaar || '";
               }
             </script>');
      ris_pck_alg.norightclick;
      htp.headclose;
      htp.bodyopen;
      htp.p(htf.anchor2('javascript:toonNaam()'
                       ,'<h1>Series Mail</h1>'));
      htp.tableopen(cattributes => 'cellspacing="0"');
    
      for r_berichtenbak in c_berichtenbak loop
        v_n_bak_volgnr := r_berichtenbak.volgnr;
      
        open c_aantal_berichten;
        fetch c_aantal_berichten
          into v_n_aantal;
        close c_aantal_berichten;
      
        htp.tablerowopen;
        htp.tabledata(htf.anchor2('javascript:toonBerichten(' ||
                                  to_char(r_berichtenbak.volgnr) || ')'
                                 ,'<img border=0 src="' || r_berichtenbak.image || '">'));
        if r_berichtenbak.volgnr <> 4 then
          htp.tabledata(htf.anchor2('javascript:toonBerichten(' ||
                                    to_char(r_berichtenbak.volgnr) || ')'
                                   ,r_berichtenbak.omschrijving || ' <b>(' ||
                                    to_char(v_n_aantal) || ')</b>'));
        else
          htp.tabledata(htf.anchor2('javascript:toonBerichten(' ||
                                    to_char(r_berichtenbak.volgnr) || ')'
                                   ,r_berichtenbak.omschrijving || '</b>'));
        end if;
        htp.tablerowclose;
      end loop;
      htp.tableclose;
    
      htp.br;
      htp.br;
    
      htp.p('<a href="javascript:nieuwBericht()">New Message</a>');
      htp.br;
      htp.br;
      htp.p('<a href="ris_pck_mail.toon_berichtenbak?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
            '&p_s_session_id=' || p_s_session_id || '&p_jaar=' || p_jaar ||
            '">Refresh</a>');
      htp.br;
      htp.br;
      htp.br;
      htp.p('<a href="ris_pck_mail.logoff?p_s_gbr_volgnr=' || p_s_gbr_volgnr ||
            '&p_s_session_id=' || p_s_session_id || '&p_jaar=' || p_jaar ||
            '" TARGET="_parent"><img border=0 src="/ris-img/logoff.gif"></a>');
    
      open c_sync_tijd;
      fetch c_sync_tijd
        into v_s_sync_tijd;
      if c_sync_tijd%NotFound then
        v_s_sync_tijd := 'Unknown';
      end if;
      close c_sync_tijd;
    
      open c_send_tijd;
      fetch c_send_tijd
        into v_s_send_tijd;
      if c_send_tijd%NotFound then
        v_s_send_tijd := 'Unknown';
      end if;
      close c_send_tijd;
    
      htp.br;
      htp.br;
      htp.tableopen(cattributes => 'cellspacing=0');
      htp.tablecaption('<b>System status:</b>');
      htp.tablerowopen;
      htp.tabledata('<b>Mail send:</b>'
                   ,cattributes => 'valign="TOP"');
      htp.tabledata(v_s_send_tijd
                   ,cattributes => 'valign="TOP"');
      htp.tablerowclose;
      htp.tablerowopen;
      htp.tabledata('<b>Mail received:</b>'
                   ,cattributes => 'valign="TOP"');
      htp.tabledata(v_s_sync_tijd
                   ,cattributes => 'valign="TOP"');
      htp.tablerowclose;
      htp.tableclose;
    
      htp.bodyclose;
      htp.htmlclose;
    end if;
  end;
  ----------------------------------------------------------------------------------------
  procedure logoff(p_s_gbr_volgnr in varchar2
                  ,p_s_session_id in varchar2
                  ,p_jaar         in varchar2) is
  
  begin
    if ris_pck_alg.controlesessionid(p_s_gbr_volgnr
