  ----------------------------------------------------------------------------------------
  procedure toon_berichten(p_s_bbk_volgnr in varchar2
                          ,p_s_gbr_volgnr in varchar2
                          ,p_s_session_id in varchar2
                          ,p_jaar         in varchar2) is
  
    v_s_bak_omschrijving ris_berichtenbak.omschrijving%type;
    v_n_aantal           number := 0;
  
    cursor c_berichtenbak is
      select omschrijving
        from ris_berichtenbak
       where volgnr = to_number(p_s_bbk_volgnr);
  
    cursor c_berichten is
      select *
        from ris_bericht
       where gbrvolgnr = to_number(p_s_gbr_volgnr) and
             bbkvolgnr = to_number(p_s_bbk_volgnr)
       order by volgnr desc;
  
  begin
    if ris_pck_alg.controlesessionid(p_s_gbr_volgnr
                                    ,p_s_session_id) then
      htp.htmlopen;
      htp.headopen;
      ris_pck_alg.titel;
      ris_pck_alg.stylesheet;
      ris_pck_alg.norightclick;
      htp.headclose;
      htp.bodyopen;
    
      open c_berichtenbak;
      fetch c_berichtenbak
        into v_s_bak_omschrijving;
      close c_berichtenbak;
    
      htp.p('<h1>' || v_s_bak_omschrijving);
    
      if p_s_bbk_volgnr = 4 then
        htp.p('<a href="ris_pck_mail.empty_deleted_items?p_s_gbr_volgnr=' ||
              p_s_gbr_volgnr || '&p_s_session_id=' || p_s_session_id || '&p_jaar=' ||
              p_jaar ||
              '"><img src="/ris-img/DeletedItems.gif" border="0">Empty Deleted Items</a>');
      end if;
    
      htp.p('</H1>');
      htp.tableopen(cattributes => 'cellspacing="0"');
      htp.tablerowopen;
      htp.tableheader('&nbsp;');
      if p_s_bbk_volgnr in ('1', '4') then
        htp.tableheader('From'
                       ,calign => 'LEFT'
                       ,cattributes => 'width="170"');
      elsif p_s_bbk_volgnr in ('2', '3') then
        htp.tableheader('To'
                       ,calign => 'LEFT'
                       ,cattributes => 'width="150"');
      end if;
    
      htp.tableheader('Subject'
                     ,calign => 'LEFT'
                     ,cattributes => 'width="200"');
    
      if p_s_bbk_volgnr in ('1', '4') then
        htp.tableheader('Received'
                       ,calign => 'LEFT');
      elsif p_s_bbk_volgnr in ('2', '3') then
        htp.tableheader('Sent'
                       ,calign => 'LEFT');
      end if;
    
      htp.tablerowclose;
    
      for r_berichten in c_berichten loop
        v_n_aantal := v_n_aantal + 1;
      
        if v_n_aantal / 2 = trunc(v_n_aantal / 2) then
          htp.tablerowopen(cattributes => 'class = "even"');
        else
          htp.tablerowopen(cattributes => 'class = "oneven"');
        end if;
      
        htp.tabledata('<a href="ris_pck_mail.bericht_detail?p_s_gbr_volgnr=' ||
                      p_s_gbr_volgnr || '&p_s_brt_volgnr=' ||
                      to_char(r_berichten.volgnr) || '&p_s_session_id=' ||
                      p_s_session_id || '&p_jaar=' || p_jaar ||
                      '" target="DETAIL"><img src="/ris-img/toonbericht.gif" border="0"></a>');
        if r_berichten.status = 'G' then
          htp.tabledata(r_berichten.adres);
          htp.tabledata(r_berichten.onderwerp);
          htp.tabledata(to_char(r_berichten.datum_bericht
                               ,'DD-MM-YYYY HH24:MI'));
        else
          htp.tabledata('<B>' || r_berichten.adres || '</B>');
          htp.tabledata('<B>' || r_berichten.onderwerp || '</B>');
          htp.tabledata('<B>' || to_char(r_berichten.datum_bericht
                                        ,'DD-MM-YYYY HH24:MI') || '</B>');
        end if;
        htp.tablerowclose;
      end loop;
    
      htp.tableclose;
      htp.bodyclose;
      htp.htmlclose;
    end if;
  end;
