  procedure nieuw_bericht(p_s_gbr_volgnr in varchar2
                         ,p_s_session_id in varchar2
                         ,p_jaar         in varchar2) is
  
    v_s_tabledata varchar2(32000);
  
    cursor c_gebruikers is
      select gbr.*
            ,tem.naam as omschrijving
            ,gbr.achternaam || ', ' || gbr.voornaam ||
             decode(gbr.tussenvoegsel
                   ,null
                   ,' '
                   ,' ' || gbr.tussenvoegsel || ' ') as naam
