  procedure verwerk_nieuw_bericht(p_s_gbr_volgnr in varchar2
                                 ,p_s_adres1     in varchar2
                                 ,p_s_adres2     in varchar2
                                 ,p_s_subject    in varchar2
                                 ,p_s_message    in varchar2
                                 ,p_s_session_id in varchar2
                                 ,p_jaar         in varchar2) is
  
    v_s_adres varchar2(100);
    v_d_datum date := sysdate;
  
    cursor c_gebruiker is
      select tem.naam || ' - ' || gbr.achternaam || ', ' || gbr.voornaam ||
             decode(gbr.tussenvoegsel
                   ,null
                   ,' '
                   ,' ' || gbr.tussenvoegsel || ' ') as naam
        from ris_gebruikers gbr
            ,ris_teams      tem
       where tem.volgnr = gbr.temvolgnr and gbr.volgnr = to_number(p_s_adres2) and
             gbr.jaar = to_number(p_jaar) and tem.jaar = to_number(p_jaar);
  
  begin
    if ris_pck_alg.controlesessionid(p_s_gbr_volgnr
                                    ,p_s_session_id) then
      if p_s_adres1 is not null then
        v_s_adres := p_s_adres1;
      else
        open c_gebruiker;
        fetch c_gebruiker
          into v_s_adres;
        close c_gebruiker;
      end if;
    
      if p_s_adres1 is not null then
        /* E-mail naar internet */
        insert into ris_bericht
          (volgnr
          ,onderwerp
          ,berichttekst
          ,adres
          ,datum_bericht
          ,gbrvolgnr
          ,bbkvolgnr
          ,status
          ,jaar)
        values
          (brt_seq.nextval
          ,p_s_subject
          ,p_s_message
          ,v_s_adres
          ,v_d_datum
          ,to_number(p_s_gbr_volgnr)
          ,2
          ,'O'
          ,to_number(p_jaar));
      else
        /* E-mail naar andere deelnemer (intern) */
        insert into ris_bericht
          (volgnr
          ,onderwerp
          ,berichttekst
          ,adres
          ,datum_bericht
          ,gbrvolgnr
          ,bbkvolgnr
          ,status
          ,jaar)
        values
          (brt_seq.nextval
          ,p_s_subject
          ,p_s_message
          ,v_s_adres
          ,v_d_datum
          ,to_number(p_s_gbr_volgnr)
          ,3
          ,'O'
          ,to_number(p_jaar));
      
        insert into ris_bericht
          (volgnr
          ,onderwerp
          ,berichttekst
          ,adres
          ,datum_bericht
          ,gbrvolgnr
          ,bbkvolgnr
          ,status
          ,jaar)
        values
          (brt_seq.nextval
          ,p_s_subject
          ,p_s_message
          ,v_s_adres
          ,v_d_datum
          ,to_number(p_s_adres2)
          ,1
          ,'O'
          ,to_number(p_jaar));
      end if;
    
      commit;
    
      htp.htmlopen;
      htp.bodyopen;
      htp.p('<script>
               parent.location.href="ris_pck_mail.frame?p_s_gbr_volgnr=' ||
            p_s_gbr_volgnr || '&p_s_type=inbox&p_s_session_id=' || p_s_session_id ||
            '&p_jaar=' || p_jaar || '";
             </script>
            ');
      htp.htmlclose;
    end if;
  end;
  ----------------------------------------------------------------------------------------
  procedure verwijder_bericht(p_s_gbr_volgnr in varchar2
                             ,p_s_brt_volgnr in varchar2
                             ,p_s_session_id in varchar2
                             ,p_s_bbk_volgnr in varchar2
                             ,p_jaar         in varchar2) is
  
  begin
    if ris_pck_alg.controlesessionid(p_s_gbr_volgnr
                                    ,p_s_session_id) then
    
      if p_s_bbk_volgnr = 4 then
        delete from ris_bericht
         where volgnr = to_number(p_s_brt_volgnr);
      else
        update ris_bericht
           set bbkvolgnr = 4
         where volgnr = to_number(p_s_brt_volgnr);
      end if;
    
      commit;
    
      htp.htmlopen;
      htp.bodyopen;
      htp.p('<script>
               parent.location.href="ris_pck_mail.frame?p_s_gbr_volgnr=' ||
            p_s_gbr_volgnr || '&p_s_type=' || p_s_bbk_volgnr || '&p_s_session_id=' ||
            p_s_session_id || '&p_jaar=' || p_jaar || '";
             </script>
            ');
      htp.htmlclose;
    
    end if;
  end;
  ----------------------------------------------------------------------------------------
  procedure empty_deleted_items(p_s_gbr_volgnr in varchar2
                               ,p_s_session_id in varchar2
                               ,p_jaar         in varchar2) is
  
  begin
    if ris_pck_alg.controlesessionid(p_s_gbr_volgnr
                                    ,p_s_session_id) then
    
      delete from ris_bericht
       where bbkvolgnr = 4 and gbrvolgnr = to_number(p_s_gbr_volgnr);
    
      commit;
    
      htp.htmlopen;
      htp.bodyopen;
      htp.p('<script>
               parent.location.href="ris_pck_mail.frame?p_s_gbr_volgnr=' ||
            p_s_gbr_volgnr || '&p_s_type=4&p_s_session_id=' || p_s_session_id ||
            '&p_jaar=' || p_jaar || '";
             </script>
            ');
      htp.htmlclose;
    
    end if;
  end;
  ----------------------------------------------------------------------------------------
end ris_pck_mail;
