        htp.p('<tr>');
        htp.p('<td><h2>Wedstrijden ' || p_soort || ' morgen</td>');
        htp.p('<td>');
        htp.p('&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<a href="ris_pck_report.startpagina?p_jaar=' ||
