  procedure toon_stand_final
  (
    p_soort in varchar2
   ,p_jaar in varchar2
  ) is
  
    v_n_aantal       number := 0;
    v_n_aantal_index number := 0;
    v_n_eindstand    number := 1;
    v_b_gevonden     boolean := false;
    v_s_naam         ris_teams.naam%type := null;
    v_s_dummy        varchar2(1) := null;
  
    cursor v_c_finale_tonen is
      select 'x'
        from ris_teams tem
       where tem.eindstand is not null
         and tem.soort = p_soort
         and tem.jaar = to_number(p_jaar);
  
    cursor v_c_finale is
      select count(1)
        from ris_teams tem
       where soort = p_soort
         and tem.jaar = to_number(p_jaar);
  
    cursor v_c_team_tonen is
      select 'x'
        from ris_teams tem
       where tem.eindstand = v_n_eindstand
         and soort = p_soort
         and tem.jaar = to_number(p_jaar);

    cursor v_c_team is
      select tem.naam as naam
        from ris_teams tem
       where tem.eindstand = v_n_eindstand
         and soort = p_soort
         and tem.jaar = to_number(p_jaar);
  
  begin
    open v_c_finale_tonen;
    fetch v_c_finale_tonen
      into v_s_dummy;
    if v_c_finale_tonen%found then
      open v_c_finale;
      fetch v_c_finale
        into v_n_aantal;
      close v_c_finale;
    
      v_n_aantal_index := 0;
      v_n_eindstand := 1;
    
      htp.p('<table cellspacing=0 class="tabel" width="100%">');
      htp.p('<tr>');
      htp.p('<th colspan=2>Eindstand / Final Ranking ' || p_jaar ||
            '</th>');
      htp.p('</tr>');
    
      while v_n_aantal_index < v_n_aantal loop

        open v_c_team_tonen;
        fetch v_c_team_tonen into v_s_dummy;
        if v_c_team_tonen%notfound then
          v_b_gevonden := false;
            htp.p('<tr>');
            htp.p('<td>' || to_char(v_n_eindstand) || '.</td>');
            htp.p('<td>-</td>');
            htp.p('</tr>');
            v_n_aantal_index := v_n_aantal_index + 1;
        else
          v_b_gevonden := true;

          for v_r_team in v_c_team loop

            htp.p('<tr>');
            htp.p('<td>' || to_char(v_n_eindstand) || '.</td>');
            htp.p('<td>' || v_r_team.naam || '</td>');
            htp.p('</tr>');
            
            v_n_aantal_index := v_n_aantal_index + 1;
          end loop;        
        end if;        
        close v_c_team_tonen;

        v_n_eindstand := v_n_eindstand + 1;

      end loop;
    
      htp.p('</table>');
      htp.br;
      show_poule_commentaar(p_soort
                           ,p_jaar);
    
    end if;
    close v_c_finale_tonen;
  
  end;
  ------------------------------------------------------------------------------------------------------------------------
  procedure show_poule_commentaar
  (
    p_soort in varchar2
   ,p_jaar in varchar2
  ) is
  
    v_s_commentaar varchar2(1000);
  
    cursor c_commentaar is
      select rpc.commentaar
        from ris_poule_commentaar rpc
       where rpc.jaar = to_number(p_jaar)
         and rpc.afkorting = p_soort;
  
  begin
    open c_commentaar;
    fetch c_commentaar
      into v_s_commentaar;
    if c_commentaar%found then
      htp.p('<table cellspacing=0 class="tabel" width="100%">');
      htp.p('<tr><td>');
      htp.p(v_s_commentaar);
      htp.p('</td></tr>');
      htp.p('</table>');
    end if;
    close c_commentaar;
  
  end;

end ris_pck_stand;
