  procedure sync_mail_berichten is
  
    cursor c_teams is
      select tem.volgnr
            ,tem.naam
            ,tem.soort
        from ris_teams tem
       where tem.volgnr not in (select mtem.volgnr
                                  from mailteams@RHP mtem) and tem.jaar = v_n_jaar;
  
    cursor c_gebruikers is
      select gbr.volgnr
            ,gbr.voornaam
            ,gbr.loginid
            ,gbr.wachtwoord
            ,gbr.tussenvoegsel
            ,gbr.achternaam
            ,gbr.temvolgnr
        from ris_gebruikers gbr
       where gbr.volgnr not in (select mgbr.volgnr
                                  from mailgebruikers@RHP mgbr) and gbr.jaar = v_n_jaar;
  
    cursor c_berichten is
      select mbrt.volgnr
            ,mbrt.onderwerp
            ,mbrt.berichttekst
            ,mbrt.adres
            ,mbrt.datum_bericht
            ,mbrt.gbrvolgnr
        from mailbericht@RHP mbrt
       where mbrt.datum_verwerkt is null;
  
  begin
    ---------------------------------------------------------------------------------------
    dbms_output.put_line('Synchroniseren van teams');
    delete from mailteams@RHP;
  
    for r_teams in c_teams loop
      insert into mailteams@RHP
        (volgnr
        ,omschrijving)
      values
        (r_teams.volgnr
        ,r_teams.soort || '-' || r_teams.naam);
      commit;
    end loop;
    ---------------------------------------------------------------------------------------
    dbms_output.put_line('Synchroniseren van gebruikers');
    delete from mailgebruikers@RHP;
  
    for r_gebruikers in c_gebruikers loop
      insert into mailgebruikers@RHP
        (volgnr
        ,loginid
        ,wachtwoord
        ,voornaam
        ,tussenvoegsel
        ,achternaam
        ,temvolgnr
        ,session_id)
      values
        (r_gebruikers.volgnr
        ,r_gebruikers.loginid
        ,r_gebruikers.wachtwoord
        ,r_gebruikers.voornaam
        ,r_gebruikers.tussenvoegsel
        ,r_gebruikers.achternaam
        ,r_gebruikers.temvolgnr
        ,null);
      commit;
    end loop;
    ---------------------------------------------------------------------------------------
    dbms_output.put_line('Synchroniseren van berichten');
    for r_berichten in c_berichten loop
      insert into ris_bericht
        (volgnr
        ,onderwerp
        ,berichttekst
        ,adres
        ,datum_bericht
        ,gbrvolgnr
        ,bbkvolgnr
        ,jaar)
