        ,r_berichten.berichttekst
        ,r_berichten.adres
        ,r_berichten.datum_bericht
        ,r_berichten.gbrvolgnr
        ,1
        ,v_n_jaar); -- inbox
    
      update mailbericht@RHP
         set datum_verwerkt = sysdate
       where volgnr = r_berichten.volgnr;
    
      commit;
    end loop;
    ---------------------------------------------------------------------------------------  
    update ris_sysvar
       set waarde = to_char(sysdate
                           ,'DD-MM-YYYY HH24:MI')
     where code = 'SYNC_TIME';
  
    commit;
    ---------------------------------------------------------------------------------------
  exception
    when others then
      dbms_output.put_line('Fout: ' || SQLCODE);
  end;
