    ris_pck_alg.header('true', p_jaar);
  
    DELETE FROM ris_wedstrijden_finale fne
     WHERE fne.thuis_nr = to_number(p_team_volgnr)
        OR fne.uit_nr = to_number(p_team_volgnr);
  
