  ------------------------------------------------------------------------------------
  procedure doe_voegtoe_wedstrijd(p_team_volgnr      in varchar2,
                                  p_datum            in varchar2,
                                  p_tijdstip         in varchar2,
                                  p_soort            in varchar2,
                                  p_team             in varchar2,
                                  p_team2            in varchar2,
                                  p_type_thuis       in varchar2 default null,
                                  p_type_uit         in varchar2 default null,
                                  p_result           in varchar2 default null,
                                  p_result2          in varchar2 default null,
                                  p_wedstrijd_nummer in varchar2,
                                  p_type_wedstrijd   in varchar2,
                                  p_opmerking        in varchar2,
                                  p_scheidsrechters  in varchar2,
                                  p_jaar             in varchar2) is
  
    v_poule_thuis    varchar2(10);
    v_poule_thuis_nr varchar2(10);
    v_thuis_wv       varchar2(10);
    v_thuis_wed      varchar2(10);
    v_thuis_nr       varchar2(10);
    v_poule_uit      varchar2(10);
    v_poule_uit_nr   varchar2(10);
    v_uit_wv         varchar2(10);
    v_uit_wed        varchar2(10);
    v_uit_nr         varchar2(10);
  
  begin
    ris_pck_alg.header('true', p_jaar);
  
    if p_type_wedstrijd = 'Poule' then
      INSERT INTO ris_wedstrijden_poule
        (volgnr,
         datum,
         wedstrijd_nummer,
         tijdstip,
         punten_voor,
         punten_tegen,
         team_volgnr,
         team2_volgnr,
         soort,
         opmerking,
         scheidsrechters,
         jaar)
      values
        (wed_seq.nextval,
         trunc(to_date(p_datum, 'DD-MM-YYYY')),
         p_wedstrijd_nummer,
         p_tijdstip,
         to_char(null),
         to_char(null),
         to_number(p_team),
         to_number(p_team2),
         p_soort,
         p_opmerking,
         p_scheidsrechters,
         to_number(p_jaar));
    elsif p_type_wedstrijd = 'Kruis' then
      INSERT INTO ris_wedstrijden_kruis
        (volgnr,
         datum,
         wedstrijd_nummer,
         tijdstip,
         punten_voor,
         punten_tegen,
         poule_thuis,
         poule_thuis_nr,
         poule_uit,
         poule_uit_nr,
         soort,
         opmerking,
         scheidsrechters,
         jaar)
      values
        (wed_seq.nextval,
         to_date(p_datum, 'DD-MM-YYYY'),
         p_wedstrijd_nummer,
         p_tijdstip,
         to_char(null),
         to_char(null),
         p_result,
         p_team,
         p_result2,
         p_team2,
         p_soort,
         p_opmerking,
         p_scheidsrechters,
         to_number(p_jaar));
    elsif p_type_wedstrijd = 'Finale' then
      INSERT INTO ris_wedstrijden_finale
        (volgnr,
         datum,
         wedstrijd_nummer,
         tijdstip,
         punten_voor,
         punten_tegen,
         thuis_wv,
         thuis_wed,
         thuis_nr,
         uit_wv,
         uit_wed,
         uit_nr,
         soort,
         opmerking,
         scheidsrechters,
         jaar)
      values
        (wed_seq.nextval,
         to_date(p_datum, 'DD-MM-YYYY'),
         p_wedstrijd_nummer,
         p_tijdstip,
         to_char(null),
         to_char(null),
         p_result,
         p_team,
         null,
         p_result2,
         p_team2,
         null,
         p_soort,
         p_opmerking,
         p_scheidsrechters,
         to_number(p_jaar));
    elsif p_type_wedstrijd = 'Demo' then
      INSERT INTO ris_wedstrijden_demo
        (volgnr,
         datum,
         wedstrijd_nummer,
         tijdstip,
         punten_voor,
         punten_tegen,
         team_thuis,
         team_uit,
         soort,
         opmerking,
         scheidsrechters,
         jaar)
      values
        (wed_seq.nextval,
         trunc(to_date(p_datum, 'DD-MM-YYYY')),
         p_wedstrijd_nummer,
         p_tijdstip,
         to_char(null),
         to_char(null),
         p_team,
         p_team2,
         p_soort,
         p_opmerking,
         p_scheidsrechters,
         to_number(p_jaar));
    elsif p_type_wedstrijd = 'Mix' then
      if p_type_thuis = 'Kruis' then
        v_poule_thuis    := p_result;
        v_poule_thuis_nr := p_team;
        -- Finale-velden op null zetten
        v_thuis_wv  := null;
        v_thuis_wed := null;
        v_thuis_nr  := null;
      end if;
      if p_type_thuis = 'Finale' then
        v_thuis_wv  := p_result;
        v_thuis_wed := p_team;
        v_thuis_nr  := null;
        -- Kruis-velden op null zetten
        v_poule_thuis    := null;
        v_poule_thuis_nr := null;
      end if;
      if p_type_uit = 'Kruis' then
        v_poule_uit    := p_result2;
        v_poule_uit_nr := p_team2;
        -- Finale-velden op null zetten
        v_uit_wv  := null;
        v_uit_wed := null;
        v_uit_nr  := null;
      end if;
      if p_type_uit = 'Finale' then
        v_uit_wv  := p_result2;
        v_uit_wed := p_team2;
        v_uit_nr  := null;
        -- Kruis-velden op null zetten
        v_poule_uit    := null;
        v_poule_uit_nr := null;
      end if;
    
      INSERT INTO ris_wedstrijden_mix
        (volgnr,
         datum,
         wedstrijd_nummer,
         tijdstip,
         punten_voor,
         punten_tegen,
         type_thuis,
         type_uit,
         poule_thuis,
         poule_thuis_nr,
         poule_uit,
         poule_uit_nr,
         thuis_wv,
         thuis_wed,
         thuis_nr,
         uit_wv,
         uit_wed,
         uit_nr,
         soort,
         opmerking,
         scheidsrechters,
         jaar)
      values
        (wed_seq.nextval,
         to_date(p_datum, 'DD-MM-YYYY'),
         p_wedstrijd_nummer,
         p_tijdstip,
         to_char(null),
         to_char(null),
         p_type_thuis,
         p_type_uit,
         v_poule_thuis,
         v_poule_thuis_nr,
         v_poule_uit,
         v_poule_uit_nr,
         v_thuis_wv,
         v_thuis_wed,
         v_thuis_nr,
         v_uit_wv,
         v_uit_wed,
         v_uit_nr,
         p_soort,
         p_opmerking,
         p_scheidsrechters,
         to_number(p_jaar));
    end if;
    commit;
  
    htp.p('<body>');
    htp.p('<script>');
    htp.p('  document.location.href="ris_pck_teams.wedstrijden_overzicht?p_team_volgnr=' ||
          p_team_volgnr || '&p_jaar=' || p_jaar || '";');
    htp.p('</script>');
  
    htp.p('</body>');
    htp.p('</html>');
  
  exception
    when others then
      ris_pck_alg.foutafhandeling('Toevoegen Wedstrijd',
                                  sqlcode,
                                  sqlerrm,
                                  'Soort: ' || p_soort);
  end;
